const requrl = ""
      $(document).ready(function(){

          $("#submit").click(function(e){
                e.preventDefault();

                var formData = new FormData(document.getElementById("upload-form"));
                $.ajax({
                type: 'POST',
                url: requrl+'/upload',
                data: formData,
                processData: false,
                contentType: false
                }).done(function(res){
                    //console.log(res.data);
                    $("#file").html(res.data.name);
                    var typeNumber = 0;
                    var errorCorrectionLevel = 'L';
                    var qr = qrcode(typeNumber, errorCorrectionLevel);
                    qr.addData(res.data.link);
                    qr.make();
                    $('#qrcode').html(qr.createSvgTag(6,4));
                    $("#input-form").fadeOut();   
                    setTimeout(function(){
                        $("#qrcode-container").fadeIn();
                    },1000);
                    
                }).fail(function(err){
                    console.log(err);
                    window.location.reload();
                }); 
          });
      });

